(1, 'H5P.Accordion', 1, 0, 24, 1, 5, 'Accordion', 'Create vertically stacked expandable items', 'Reduce the amount of text presented to readers by using this responsive accordion. Readers decide which headlines to take a closer look at by expanding the title. Excellent for providing an overview with optional in-depth explanations.', 'https://h5p.org/sites/default/files/accordion.svg', 1488282378, 1589176511, 1, 9, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/accordion-01.png?itok=qnXarBZ5\",\"alt\":\"Collapsed\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/accordion-02.png?itok=lnTobl4C\",\"alt\":\"Expanded\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/accordion', 'https://h5p.org/accordion-tutorial', '[\"accordion\",\"collapsible text\"]', '[]', 'Joubel'),
(2, 'H5P.ArithmeticQuiz', 1, 1, 12, 1, 0, 'Arithmetic Quiz', 'Create time-based arithmetic quizzes', 'Create arithmetic quizzes consisting of multiple choice questions. As an author, all you have to do is decide the type and length of the quiz. Users keep track of score and time spent when solving the quiz.', 'https://h5p.org/sites/default/files/arithmetic%20quiz.svg', 1488283418, 1563264938, 1, 21, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-01.png?itok=w00QVe4N\",\"alt\":\"Arithmetic quiz start screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-02.png?itok=k6nal6kx\",\"alt\":\"Arithmetic quiz question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-03.png?itok=oHISx8VF\",\"alt\":\"Arithmetic quiz correct answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-04.png?itok=JIcoDl58\",\"alt\":\"Arithmetic quiz end screen\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/arithmetic-quiz', '', '[\"math\",\"arithmetic quiz\",\"countdown\"]', '[\"Games\",\"Questions\"]', 'Joubel'),
(3, 'H5P.Chart', 1, 2, 16, 1, 21, 'Chart', 'Quickly generate bar and pie charts', 'Need to present simple statistical data graphically without creating the artwork manually? Chart is your answer.', 'https://h5p.org/sites/default/files/chart.svg', 1488283592, 1589176511, 1, 41, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-01_0.png?itok=iRQawZtl\",\"alt\":\"Pie chart view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-02_0.png?itok=6G-3NLs-\",\"alt\":\"Bar chart view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-03_0.png?itok=dGg4KIiV\",\"alt\":\"Bar chart view\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/chart', '', '[\"chart\",\"bar chart\",\"pie chart\",\"graph\"]', '[\"Multimedia\"]', 'Joubel'),
(4, 'H5P.Collage', 0, 3, 14, 1, 7, 'Collage', 'Create a collage of multiple images', 'The Collage tool allows you to organize images into a soothing composition. ', 'https://h5p.org/sites/default/files/college.svg', 1488374773, 1589176511, 1, 36, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-04_0.png?itok=Ix3CDsjz\",\"alt\":\"Collage editor with no images\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-03_0.png?itok=idf9cgYV\",\"alt\":\"Collage editor with three images\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-01_0.png?itok=-UtwiYRz\",\"alt\":\"A collage containing 3 images of flowers\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-02_0.png?itok=aTHRDus1\",\"alt\":\"A collage containing 4 images of snowy mountains\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/collage', '', '[\"collage\",\"image\"]', '[\"Multimedia\"]', 'Joubel'),
(5, 'H5P.Column', 1, 12, 0, 1, 19, 'Column', 'Organize H5P content into a column layout', 'Organize your content type into a column layout with H5P Column. Content types that address similar material or share a common theme can now be grouped together to create a coherent learning experience. In addition, authors are free to be creative by combining almost all of the existing H5P content types.', 'https://h5p.org/sites/default/files/column.svg', 1488375032, 1591126826, 1, 6, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/column-01.png?itok=37ibbzhk\",\"alt\":\"A column containing an image, a text block and a question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/column-02.png?itok=e2km4R4C\",\"alt\":\"A column containing different content types\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/column-03.png?itok=nilYMNyC\",\"alt\":\"A drag and drop inside a column\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/column', '', '[\"column\",\"layout\",\"compound\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(6, 'H5P.CoursePresentation', 1, 22, 0, 1, 23, 'Course Presentation', 'Create a presentation with interactive slides', 'Course presentations consist of slides with multimedia, text, and many different types of interactions like interactive summaries, multiple choice questions and interactive videos. Learners can experience new interactive learning material and test their knowledge and memory in Course Presentations. As always with H5P, content is editable in web browsers, and the Course Presentation activity type includes a WYSIWYG drag and drop based authoring tool. A typical use of the Course Presentation activity is to use a few slides to introduce a subject and follow these with a few more slides in which the users knowledge is tested. Course Presentations may however be used in many different ways, including as a presentation tool for use in the classroom, or as a game where the usual navigation is replaced with navigation buttons on top of the slides to let the user make choices and see the consequences of their choices.', 'https://h5p.org/sites/default/files/course%20presentation.svg', 1488375267, 1591126826, 1, 1, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-01.png?itok=ilSK_sEu\",\"alt\":\"Adding an image to the editor\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-02_0.png?itok=NrkD9l5t\",\"alt\":\"A single Choice Set inside the editor\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-03.png?itok=cJiwaVzi\",\"alt\":\"A single Choice Set and an image of a cloudberry inside the editor\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-04_0.png?itok=CIQbHd-t\",\"alt\":\"First slide of a presentation containing text and a clickable image\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-05_0.png?itok=z-6dFoL1\",\"alt\":\"Summary slide\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-06_0.png?itok=gHNw6Vt0\",\"alt\":\"Active surface mode\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-07_0.png?itok=oAOD70di\",\"alt\":\"A slide containing video and text\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/presentation', 'https://h5p.org/tutorial-course-presentation', '[\"course presentation\",\"slides\",\"powerpoint\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(7, 'H5P.Dialogcards', 1, 8, 4, 1, 15, 'Dialog Cards', 'Create text-based turning cards', 'Dialog cards can be used as a drill to help learners memorize words, expressions or sentences. On the front of the card, there\'s a hint for a word or expression. By turning the card the learner reveals a corresponding word or expression. Dialog cards can be used in language learning, to present math problems or help learners remember facts such as historical events, formulas or names.', 'https://h5p.org/sites/default/files/dialog%20cards%20.svg', 1488375538, 1589967106, 1, 10, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-01_0.png?itok=OgMQk8D5\",\"alt\":\"Dialog card - front\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-02_0.png?itok=Pv49ouxC\",\"alt\":\"Dialog card - back\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-03.png?itok=8wwQDVUw\",\"alt\":\"Dialog card - front\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-04.png?itok=8VFY5QY3\",\"alt\":\"Dialog card - back\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/dialog-cards', 'https://h5p.org/tutorial-dialog-cards', '[\"dialog cards\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(8, 'H5P.DocumentationTool', 1, 8, 6, 1, 19, 'Documentation Tool', 'Create a form wizard with text export', 'The documentation tool aims to make it easy to create assessment wizards for goal driven activities. It can also be used as a form wizard. While editing, the author can add multiple steps to the wizard. In each step, the author can define which content goes into that step. Content can be plain text, input fields, goal definition and goal assessment. Once published, the end user will be taken through the steps of the wizard. On the last step of the wizard, the user can generate a document with all the input that has been submitted. This document can be downloaded. The Documentation tool is fully responsive and works great on smaller screens as well as on your desktop.', 'https://h5p.org/sites/default/files/documentation%20tool%20.svg', 1488375711, 1593525535, 1, 14, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-01.png?itok=mIhHTIX3\",\"alt\":\"User\'s view \\u2013 document your project\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-02.png?itok=WCPC1IpT\",\"alt\":\"User\'s view \\u2013 Goals\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-03.png?itok=GWEa3LZf\",\"alt\":\"User\'s view \\u2013 Done\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-04.png?itok=yFf2Qa-n\",\"alt\":\"User\'s view \\u2013 Document your project\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/documentation-tool', 'https://h5p.org/tutorial-documentation-tool', '[\"dialog cards\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(9, 'H5P.DragQuestion', 1, 13, 11, 1, 23, 'Drag and Drop', 'Create drag and drop tasks with images', 'Drag and drop question enables the learner to associate two or more elements and to make logical connections in a visual way. Create Drag and drop questions using both text and images as draggable alternatives. H5P Drag and drop questions support one-to-one, one-to-many, many-to-one and many-to-many relations between questions and answers. ', 'https://h5p.org/sites/default/files/drag%20and%20drop%20.svg', 1488376151, 1591785629, 1, 7, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-01.png?itok=xS6wYXCj\",\"alt\":\"A drag and drop with one dropzone (strawberry) and three draggables\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-03.png?itok=BMTD9pmT\",\"alt\":\"A drag and drop where the learner shall match flags and country names\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-04.png?itok=_QbzXvhm\",\"alt\":\"A drag and drop where the learner shall match images of berries with their names\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-02.png?itok=xcDAfXTD\",\"alt\":\"A drag and drop where the learner shall drag verbs, adjectives and nouns in the correct places\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-06.png?itok=jONaT2bA\",\"alt\":\"A drag and drop where the learner shall place the draggables on spots on the background image\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-05.png?itok=wngjR93X\",\"alt\":\"The drag and drop result view\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/drag-and-drop', 'https://h5p.org/tutorial-drag-and-drop-question', '[\"drag and drop\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(10, 'H5P.DragText', 1, 8, 9, 1, 19, 'Drag the Words', 'Create text-based drag and drop tasks', 'Drag the Words allows content designers to create textual expressions with missing pieces of text. The end user drags a missing piece of text to its correct place, to form a complete expression. May be used to check if the user remembers a text she has read, or if she understands something. Helps the user think through a text. It\'s super easy to create a drag the words task. The editor just writes the text and encloses the words that are to be draggable with asterisk signs like *draggableWord*.', 'https://h5p.org/sites/default/files/drag%20the%20words.svg', 1488376477, 1593525535, 1, 5, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-the-words-01.png?itok=dpMCvPPe\",\"alt\":\"Drag text - initial state.\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-the-words-02.png?itok=njr_aEqB\",\"alt\":\"Drag text after clicking the \\\"check\\\" button\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-the-words-03.png?itok=2xeHtKBa\",\"alt\":\"Tip in drag text\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/drag-the-words', 'https://h5p.org/documentation/content-author-guide/tutorials-for-authors/drag-the-words', '[\"drag\",\"words\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(11, 'H5P.Blanks', 1, 12, 5, 1, 19, 'Fill in the Blanks', 'Create a task with missing words in a text', 'Learners fill in the missing words in a text. The learner is shown a solution after filling in all the missing words, or after each word depending on settings. Authors enter text and mark words to be replaced with an asterix. In addition to native and second language learning, Fill in the blanks can be used to test the learner\'s ability to reproduce facts or produce mathematical inferences.', 'https://h5p.org/sites/default/files/fill%20in%20the%20blanks.svg', 1488376747, 1591126826, 1, 4, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-01.png?itok=EwyBhMVL\",\"alt\":\"The student\'s initial view - empty blanks\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-02.png?itok=ByI6y9m-\",\"alt\":\"The result view showing corrects and wrongs\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-03.png?itok=49rZ2yTY\",\"alt\":\"Tip inside blanks\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-04.png?itok=m8MnJh16\",\"alt\":\"A blank with \\\"Automatically check answers after input\\\" enabled\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/fill-in-the-blanks', 'https://h5p.org/tutorial-fill-in-the-blanks', '[\"fill\",\"blanks\",\"text\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(12, 'H5P.ImageHotspotQuestion', 1, 8, 8, 1, 23, 'Find the Hotspot', 'Create image hotspots for users to find', 'This content type allows end users to press somewhere on an image and get feedback on whether that was correct or incorrect according to the task description. The author uploads an image and defines various hotspots corresponding to details or sections of the image. Hotspots can either be defined as correct or incorrect, and the author provides appropriate feedback text in both cases. The author can also define a feedback if the end user presses somewhere which is neither defined as a correct nor incorrect hotspot.', 'https://h5p.org/sites/default/files/find%20the%20hotspot.svg', 1488377636, 1591126826, 0, 26, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-hotspot-01.png?itok=G5zJHEh0\",\"alt\":\"Learner\'s view - correct answer is selected\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-hotspot-02.png?itok=mNIZ5J08\",\"alt\":\"Editor view\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/image-hotspot-question', '', '[\"find\",\"hotspot\",\"image\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(13, 'H5P.GuessTheAnswer', 1, 4, 8, 1, 19, 'Guess the Answer', 'Create an image with a question and answer', 'This content type allows authors to upload an image and add a suitable description. End users can guess the answer and press the bar below the image to reveal the correct answer.', 'https://h5p.org/sites/default/files/guess%20the%20answer.svg', 1488377905, 1596188027, 1, 35, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/guess-the-answer-01.png?itok=cKWhsf7W\",\"alt\":\"Learner\'s initial view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/guess-the-answer-02.png?itok=k9g-O8UW\",\"alt\":\"Learner\'s view revealing the correct answer\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/guess-the-answer', '', '[\"guess\",\"answer\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(14, 'H5P.IFrameEmbed', 1, 0, 25, 1, 0, 'Iframe Embedder', 'Embed from a url or a set of files', 'The Iframe embedder makes it easy to make an H5P of already existing JavaScript applications.', 'https://h5p.org/sites/default/files/iframe%20embedder.svg', 1488378484, 1491475605, 0, 18, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/iFrame-embedder-01.png?itok=CehXfNpd\",\"alt\":\"End user\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/iFrame-embedder-02.png?itok=p5V0tHcY\",\"alt\":\"End user\'s view\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/iframe-embedder', '', '[\"embed\",\"iframe\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(15, 'H5P.InteractiveVideo', 1, 22, 2, 1, 23, 'Interactive Video', 'Create videos enriched with interactions', 'Add interactivity to your video with explanations, extra pictures, tables, Fill in the Blank and multiple choice questions. Quiz questions support adaptivity, meaning that you can jump to another part of the video based on the user\'s input.  Interactive summaries can be added at the end of the video. Interactive videos are created and edited using the H5P authoring tool in a standard web browser.', 'https://h5p.org/sites/default/files/interactive%20video.svg', 1488379462, 1599553441, 1, 0, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-01.png?itok=WgAmEzb0\",\"alt\":\"Interactive video start screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-02.png?itok=9P0-eiaB\",\"alt\":\"The editor with a multiple choice question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-03.png?itok=2X03y0pe\",\"alt\":\"Inside the editor showing the multiple choice editor\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-04.png?itok=NtKe_dhK\",\"alt\":\"Bookmarks menu\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-05.png?itok=Xy-1r3Dt\",\"alt\":\"Drag and drop inside the video\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-06.png?itok=aVysZc0i\",\"alt\":\"Summary at the end of the video\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/interactive-video', 'https://h5p.org/tutorial-interactive-video', '[\"interactive\",\"video\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(16, 'H5P.MarkTheWords', 1, 9, 9, 1, 19, 'Mark the Words', 'Create a task where users highlight words', 'Mark the words allows content designers to create textual expressions with a defined set of correct words. The end user highlights words according to the task description and is given a score. For the editor it is super easy to create a click the words challenge. The editor types in the text and encloses the words that the user is supposed to click, the right answers, in asterix like *correctWord*.', 'https://h5p.org/sites/default/files/mark%20the%20words.svg', 1488379610, 1589176511, 1, 16, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/mark-the-words-01_0.png?itok=FTe4BK8x\",\"alt\":\"Learner\'s initial view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/mark-the-words-03_0.png?itok=veoD8bpQ\",\"alt\":\"Learner\'s view - showing solution\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/mark-the-words', 'https://h5p.org/documentation/content-author-guide/tutorials-for-authors/mark-the-words', '[\"mark\",\"words\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(17, 'H5P.MemoryGame', 1, 3, 5, 1, 20, 'Memory Game', 'Create the classic image pairing game', 'Create your own memory games and test the memory of your site\'s users with this simple yet beautiful HTML5 game.', 'https://h5p.org/sites/default/files/memmory%20game.svg', 1488379742, 1563265424, 1, 12, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/memmory-game-02.png?itok=wrU2ytID\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/memmory-game-03.png?itok=dCb0YlGe\",\"alt\":\"Student\'s view - feedback after the match\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/memmory-game-01.png?itok=AeOPD5HE\",\"alt\":\"Student\'s view - matching two different images\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/memory-game', 'https://h5p.org/documentation/content-author-guide/tutorials-for-authors/memory-game', '[\"memory\",\"game\",\"cards\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(18, 'H5P.MultiChoice', 1, 14, 4, 1, 19, 'Multiple Choice', 'Create flexible multiple choice questions', 'Multiple Choice questions can be an effective assesment tool. The learner is given immediate performance feedback. The H5P Multiple Choice questions can have a single or multiple correct options per question.', 'https://h5p.org/sites/default/files/multiple%20choice.svg', 1488379865, 1589176511, 1, 2, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-01.png?itok=CiwAg_Sn\",\"alt\":\"Learner\'s initial view (radio buttons)\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-02.png?itok=f5hGmJWk\",\"alt\":\"Learner answered correct\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-03.png?itok=R7zb6rTZ\",\"alt\":\"Learner\'s initial view (check buttons)\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-04.png?itok=eSNsVzFo\",\"alt\":\"Learner has clicked the \\\"check\\\" button\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/multichoice', 'https://h5p.org/tutorial-multichoice-question', '[\"multiple\",\"choice\",\"quiz\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(19, 'H5P.PersonalityQuiz', 1, 0, 8, 1, 0, 'Personality Quiz', 'Create personality quizzes', 'In this content type, the author defines a series of questions with alternatives, where each alternative is matched against one or more personalities. At the end of the quiz, the end user will see which personality matches the best. There are several ways of making this quiz visually appealing, by eg. representing questions, alternatives, and personalities using images.', 'https://h5p.org/sites/default/files/personality%20quiz.svg', 1488380090, 1491487592, 0, 38, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-01.png?itok=ygSGXtZF\",\"alt\":\"Student\'s view - starting screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-02.png?itok=79jwUFNT\",\"alt\":\"Student\'s view - question 1\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-03.png?itok=lDhEO4KA\",\"alt\":\"Student\'s view - question 2\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-04.png?itok=1zBGhax9\",\"alt\":\"Student\'s view - question 3\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-05.png?itok=rdoNJ4-j\",\"alt\":\"Student\'s view - end screen\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/personality-quiz', '', '[\"personality\",\"quiz\"]', '[\"Multimedia\",\"Questions\"]', 'Lumenia'),
(20, 'H5P.Questionnaire', 1, 3, 0, 1, 0, 'Questionnaire', 'Create a questionnaire to receive feedback', 'Gain feedback and ask open ended questions in Interactive Videos and other content types with Questionnaire. Questionnaire makes the user\'s answers available via an xAPI integration. This means that website owners may store the answers in many different ways. Answers may be stored in an LRS, the sites own custom storage or a script can fetch the e-mail address and use it to send the user an e-mail. On H5P.org answers are stored in Google Analytics.', 'https://h5p.org/sites/default/files/questionaire.svg', 1488380236, 1491487615, 0, 39, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-01.png?itok=zVNElQ9C\",\"alt\":\"Student\'s view - question 1\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-02.png?itok=SdSQGHbW\",\"alt\":\"Student\'s view - question 2\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-03.png?itok=sNnbfkv_\",\"alt\":\"Student\'s view - question 3\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-04.png?itok=J8eZGmV0\",\"alt\":\"Student\'s view - end screen\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/questionnaire', '', '[\"questionnaire\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(21, 'H5P.QuestionSet', 1, 17, 2, 1, 19, 'Quiz (Question Set)', 'Create a sequence of various question types', 'Question Set is your typical quiz content type. It allows the learner to solve a sequence of various question types such as Multichoice, Drag and drop and Fill in the blanks in a Question set. As an author, there are many settings you can use to make it behave just the way you want it to. You may, for instance, customize the Question set with background images and define a pass percentage for the learner. The Question Set also allows you to add videos that are played at the end. One video for success, another if the learner fails the test. This might motivate learners to try again if they fail so that they get to see the success video.', 'https://h5p.org/sites/default/files/question-set.svg', 1488380337, 1589176511, 1, 3, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-01.png?itok=7xWQV7Vm\",\"alt\":\"A multiple choice inside a question set\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-02_0.png?itok=ku46xHQX\",\"alt\":\"A drag and drop inside a question set\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-03.png?itok=n1cnLs5j\",\"alt\":\"A multiple choice inside a question set\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-04.png?itok=TrmmsdwT\",\"alt\":\"The summary page\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/question-set', 'https://h5p.org/tutorial-question-set', '[\"question set\",\"collection\",\"quiz\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(22, 'H5P.SingleChoiceSet', 1, 11, 9, 1, 19, 'Single Choice Set', 'Create questions with one correct answer', 'Single choice set allows content designers to create question sets with one correct answer per question. The end user gets immediate feedback after submitting each answer.', 'https://h5p.org/sites/default/files/single%20choice%20set.svg', 1488380512, 1596180071, 1, 11, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-01.png?itok=IzXoN6tp\",\"alt\":\"Learner\'s initial view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-02.png?itok=yrj9-jB0\",\"alt\":\"Correct answer given for an alternative\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-03.png?itok=o-b5R46U\",\"alt\":\"Summary page\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-04.png?itok=0BlfuOy3\",\"alt\":\"Showing solution\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/single-choice-set', 'https://h5p.org/documentation/content-author-guide/tutorials-for-authors/single-choice-set', '[\"collection\",\"quiz\",\"choice\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(23, 'H5P.Summary', 1, 10, 8, 1, 19, 'Summary', 'Create tasks with a list of statements', 'Summaries help the learner remember key information in a text, video or presentation, by actively buliding a summary about the topic at hand. When the learner has completed a summary, a complete list of key statements about the topic is shown.', 'https://h5p.org/sites/default/files/summary.svg', 1488380637, 1592396693, 1, 30, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-01.png?itok=ICk8qnxS\",\"alt\":\"Learner\'s initial view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-02.png?itok=DY9wdA0S\",\"alt\":\"Wrong answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-03.png?itok=nWaNT-Ll\",\"alt\":\"First correct statement found\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-04.png?itok=EYtv3B7n\",\"alt\":\"Second correct statement found\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-05.png?itok=tVHuEHqv\",\"alt\":\"Summary\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/summary', 'https://h5p.org/tutorial-summary', '[\"summary\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(24, 'H5P.Timeline', 1, 1, 19, 1, 0, 'Timeline', 'Create a timeline of events with multimedia', 'This is Timeline.js developed by Knight Lab, packaged as an H5P content type in order to make timelines easily editable, shareable and reuseable. The Timeline content type allows you to place a sequence of events in a chronological order. For each event you may add images and texts. You may also include assets from Twitter, YouTube, Flickr, Vimeo, Google Maps and SoundCloud.', 'https://h5p.org/sites/default/files/timeline.svg', 1488380771, 1491481225, 0, 22, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/timeline-01.png?itok=6BO75od2\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/timeline-02.png?itok=-SsDe4Zb\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/timeline-03.png?itok=Fmbxyxix\",\"alt\":\"Student\'s view - full screen preview\"}]', '{\"id\":\"MPL2\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/timeline', 'https://h5p.org/documentation/content-author-guide/tutorials-for-authors/timeline', '[\"timeline\",\"slider\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(25, 'H5P.TrueFalse', 1, 6, 4, 1, 19, 'True/False Question', 'Create True/False questions', 'True/False Question is a simple and straightforward content type that can work by itself or be inserted into other content types such as Course Presentation. A more complex question can be created by adding an image or a video.', 'https://h5p.org/sites/default/files/true%20false.svg', 1488380892, 1591126826, 1, 15, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-01.png?itok=LKXT7ZAG\",\"alt\":\"Learner\'s initial view (image and question)\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-02.png?itok=5AvPOokc\",\"alt\":\"Correct answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-03.png?itok=YCVfBTig\",\"alt\":\"Wrong answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-04.png?itok=88ogDrvt\",\"alt\":\"Learner\'s initial view\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/true-false', '', '[\"true\",\"false\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(26, 'H5P.ImageHotspots', 1, 8, 3, 1, 19, 'Image Hotspots', 'Create an image with multiple info hotspots', 'Image hotspots makes it possible to create an image with interactive hotspots. When the user presses a hotspot, a popup containing a header and text or video is displayed. Using the H5P editor, you may add as many hotspots as you like.', 'https://h5p.org/sites/default/files/image%20hotpots.svg', 1491400692, 1589176511, 1, 8, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-hotspots-01.png?itok=-GthrRP1\",\"alt\":\"View - hotspots\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-hotspots-02.png?itok=AP-FWcHh\",\"alt\":\"View - video as a hotspot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-hotspots-03.png?itok=Zt1V7UYE\",\"alt\":\"View - text as a hotspot\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/image-hotspots', 'https://h5p.org/tutorial-image-hotspots', '[\"image\",\"hotspot\"]', '[\"Multimedia\"]', 'Joubel'),
(27, 'H5P.ImageMultipleHotspotQuestion', 1, 0, 1, 1, 6, 'Find Multiple Hotspots', 'Create many hotspots for users to find', 'A free HTML5 based question type allowing creatives to create an image based test where the learner is to find the correct spots on an image. Use this content type with the H5P plugin for WordPress, Moodle or Drupal to challenge your users.', 'https://h5p.org/sites/default/files/find%20multiple%20hotspots.svg', 1491482530, 1591348523, 0, 24, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-hotspots-01.png?itok=Gbi-unNy\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-hotspots-02.png?itok=ctq2RdVL\",\"alt\":\"Authoring experience - adding hotspots\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-hotspots-03.png?itok=uAshALHJ\",\"alt\":\"User\'s view\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/find-multiple-hotspots', '', '[]', '[]', 'lukemuller'),
(28, 'H5P.ImageJuxtaposition', 1, 4, 1, 1, 15, 'Image Juxtaposition', 'Create interactive images', 'A free HTML5-based image content type that allows users to compare two images interactively. Tell your image stories with H5P and Image Juxtaposition on WordPress, Moodle or Drupal.', 'https://h5p.org/sites/default/files/before-after-image_0.svg', 1491482694, 1583849396, 0, 29, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-juxtaposition-01.png?itok=EmBa43Us\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-juxtaposition-02.png?itok=idPJFSr2\",\"alt\":\"User\'s view\"}]', '{\"id\":\"MPL2\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/image-juxtaposition', '', '[]', '[\"Multimedia\"]', 'otacke'),
(29, 'H5P.Audio', 1, 4, 3, 1, 19, 'Audio', 'Upload an audio recording', 'Upload an audio recording in .mp3, .wav, .ogg or provide the link for an audio recording.', 'https://h5p.org/sites/default/files/audio.svg', 1491918006, 1589176511, 1, 19, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-01.png?itok=z6wayrwd\",\"alt\":\"Audio player\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-02.png?itok=NdFP2pcq\",\"alt\":\"Audio player muted\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/audio', '', '[]', '[\"Multimedia\"]', 'Joubel'),
(30, 'H5P.AudioRecorder', 1, 0, 18, 1, 12, 'Audio Recorder', 'Create an audio recording', 'An HTML5 audio recorder. Record your voice and play back or download a .wav file of your recording. Use the H5P plugin to create the H5P Audio Recorder to your Drupal, Wordpress or Moodle site. ', 'https://h5p.org/sites/default/files/icon_1.svg', 1493377972, 1563264977, 0, 32, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-recorder-1.png?itok=vAcuqLSA\",\"alt\":\"Start screen for audio recorder\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-recorder-2.png?itok=qoLWgAaq\",\"alt\":\"Recording screen for audio recorder\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-recorder-3.png?itok=EnH5rwO4\",\"alt\":\"Finish screen for audio recorder\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/audio-recorder', '', '[\"audio\",\"recorder\"]', '[\"Multimedia\"]', 'Joubel'),
(31, 'H5P.SpeakTheWords', 1, 3, 8, 1, 14, 'Speak the Words', 'Answer a question using your voice (Chrome only)', 'Speak the Words is only supported in browsers that implements the Web Speech API (Chrome browsers, except on iOS). You need a microphone to answer the question. Ask a question to users and make them answer using their voice. You can choose multiple correct answers. The user will be able to see what his words were interpreted as, and how close it was to the correct answers.', 'https://h5p.org/sites/default/files/speak%20the%20words_0.svg', 1493897505, 1506502822, 0, 25, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/QuestionPagev2.png?itok=WYSV9ON0\",\"alt\":\"Question text and answer with microphone screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/ShowSolutionPagev2_0.png?itok=vUTAG0RT\",\"alt\":\"Interpreted and correct answers screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/CorrectSolutionPage.png?itok=poT-tQ6Z\",\"alt\":\"Correct answer screen\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/node/72682', '', '[\"language\",\"translation\",\"voice\",\"recognition\",\"speech\",\"mic\",\"microphone\"]', '[\"Questions\"]', 'thomasmars'),
(32, 'H5P.Agamotto', 1, 5, 2, 1, 19, 'Agamotto (Image Blender)', 'Present a sequence of images and explanations', 'Present a sequence of images that people are supposed to look at one after the other, e.g. photos of an item that changes over time, schematics or maps that are organized in different layers or images that reveal more and more details.', 'https://h5p.org/sites/default/files/icon_2.svg', 1494963576, 1585154637, 0, 33, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/agamotto-02.png?itok=xlA7OqvC\",\"alt\":\"Agamotto shown as a layered map\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/agamotto-01.png?itok=nxg6d8eA\",\"alt\":\"Agamotto transforming between different seasons\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/node/79243', 'https://h5p.org/agamotto-tutorial', '[\"image\",\"images\",\"transition\"]', '[\"Multimedia\"]', 'otacke'),
(33, 'H5P.ImageSequencing', 1, 1, 0, 1, 12, 'Image Sequencing', 'Place images in the correct order', 'A free HTML5 based image sequencing content type that allows authors to add a sequence of their own images (and optional image description) to the game in a particular order. The order of the images will be randomized and players will have to reorder them based on the task description. ', 'https://h5p.org/sites/default/files/icon_3.svg', 1503485910, 1560850171, 0, 27, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/Selection_386.png?itok=HzsvJWBU\",\"alt\":\"Initial screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/Selection_387.png?itok=m28lDPwX\",\"alt\":\"Drag and drop images\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/Selection_388.png?itok=IGV1PN-F\",\"alt\":\"Result screen when one or more is incorrect\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/Selection_389.png?itok=VZQIfLEN\",\"alt\":\"Result screen when all image are correctly ordered\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/content-types/image-sequencing', 'https://h5p.org/tutorial-image-sequencing', '[\"image\",\"sequence\",\"order\"]', '[\"Multimedia\",\"Questions\"]', 'jithin'),
(34, 'H5P.Flashcards', 1, 5, 18, 1, 4, 'Flashcards', 'Create stylish and modern flashcards', 'This content type allows authors to create a single flash card or a set of flashcards, where each card has images paired with questions and answers. Learners are required to fill in the text field and then check the correctness of their solution.', 'https://h5p.org/sites/default/files/icon_4.svg', 1503910719, 1594307364, 1, 13, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/flashcards-01_0.png?itok=5xeC6hkY\",\"alt\":\"Learner has not given any answer yet\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/flashcards-02_0.png?itok=DQTjS65M\",\"alt\":\"Correct answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/flashcards-03_0.png?itok=833omhR4\",\"alt\":\"Wrong answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/flashcards-04.png?itok=6S_kGNS4\",\"alt\":\"Summary\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/flashcards', 'https://h5p.org/tutorial-flashcards', '[\"cards\",\"animation\"]', '[\"Games\",\"Multimedia\",\"Questions\"]', 'Joubel');
INSERT INTO `wp_h5p_libraries_hub_cache` (`id`, `machine_name`, `major_version`, `minor_version`, `patch_version`, `h5p_major_version`, `h5p_minor_version`, `title`, `summary`, `description`, `icon`, `created_at`, `updated_at`, `is_recommended`, `popularity`, `screenshots`, `license`, `example`, `tutorial`, `keywords`, `categories`, `owner`) VALUES 
(35, 'H5P.SpeakTheWordsSet', 1, 1, 8, 1, 14, 'Speak the Words Set', 'Create a series of questions answered by speech (Chrome only)', 'Speak the Words Set is only supported in browsers that implement the Web Speech API (Chrome browsers, except on ios). You need a microphone to answer the question. Create a set of questions that learners can answer using their voice. you can choose multiple correct answers. The user will be able to see what his words were interpreted as, and how close it was to the correct answers.', 'https://h5p.org/sites/default/files/speak%20the%20words%20set.svg', 1506503229, 1506504400, 0, 23, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01.png?itok=92yvDMEt\",\"alt\":\"An image showing the start screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-02.png?itok=SSpDaEf6\",\"alt\":\"An image showing the question screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-03.png?itok=QRR1KWn3\",\"alt\":\"An image showing the recording taking place\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-04.png?itok=3vjLOvF3\",\"alt\":\"An image showing a correct answer is given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-05.png?itok=74miXXWG\",\"alt\":\"An image showing the solution view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-06.png?itok=UBAPLNJI\",\"alt\":\"An image showing the finish screen\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/speak-the-words-set', '', '[\"language\",\"translation\",\"voice\",\"quiz\",\"set\",\"recognition\",\"mic\",\"microphone\"]', '[\"Multimedia\",\"Questions\"]', 'Joubel'),
(36, 'H5P.ImageSlider', 1, 1, 0, 1, 14, 'Image Slider', 'Easily create an Image Slider', 'Present your images in an appealing way with ease. Authors just have to upload images and provide alternative texts for the images. \r\n\r\nThe next two images are always preloaded so switching between images will usually be snappy with no delay for loading the next image.\r\n\r\nImages may be experienced as part of the page or in full-screen mode. When used as part of the page the system will pick a fixed aspect ratio depending on the images being used. Authors may decide to handle aspect ratios differently.', 'https://h5p.org/sites/default/files/icon_5.svg', 1508146690, 1563265378, 1, 17, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_0.png?itok=PRJNeyt3\",\"alt\":\"Preview using full width\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-02_0.png?itok=NXMcjo0p\",\"alt\":\"Preview with a portrait image\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-03_0.png?itok=jqQysnpP\",\"alt\":\"Full-screen preview\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/image-slider', '', '[\"image\",\"images\",\"slider\",\"carousel\"]', '[\"Multimedia\"]', 'falcon'),
(37, 'H5P.Essay', 1, 2, 3, 1, 13, 'Essay', 'Create Essay with instant feedback', 'In this content type, the author defines a set of keywords that represent crucial aspects of a topic. These keywords are matched against a text that students have composed and can be used to immediately provide feedback - either suggesting to revise certain topic details if a keyword is missing or, confirming the student\'s ideas if the text contains a keyword.\r\n', 'https://h5p.org/sites/default/files/icon_6.svg', 1515752007, 1586415162, 0, 20, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/essay-01.png?itok=PJmHwrPp\",\"alt\":\"Initial view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/essay-02.png?itok=b8u7DK0a\",\"alt\":\"Showing sample (show solution clicked)\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/essay-03.png?itok=m8BrMhVW\",\"alt\":\"Feedback given\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/content-types/essay', 'https://h5p.org/tutorial-essay', '[\"Essay\"]', '[]', 'otacke'),
(38, 'H5P.ImagePair', 1, 4, 0, 1, 14, 'Image Pairing', 'Drag and drop image matching game', 'Image pairing is a simple and effective activity that require learners to match pairs of images. Since it is not required for both images in a pair to be the same, authors are also able to test the understanding of a relation between two different images. ', 'https://h5p.org/sites/default/files/icon_7.svg', 1525354557, 1525437284, 0, 31, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-pairing-01.png?itok=6CRqBI7P\",\"alt\":\"Initial screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-pairing-02.png?itok=ZgStVtVj\",\"alt\":\"After check - with errors\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-pairing-03.png?itok=yIpQuIMP\",\"alt\":\"In action\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/image-pairing', '', '[\"image images matching pair\"]', '[]', 'jithin'),
(39, 'H5P.Dictation', 1, 0, 3, 1, 13, 'Dictation', 'Create a dictation with instant feedback', 'You can add audio samples containing a sentence for dictation and enter the correct transcription. Your students can listen to the samples and enter what they have heard in to a text field. Their answers will be evaluated automatically. Several options will allow you to control the exercise\'s difficulty. You can optionally add a second audio sample for a sentence that could hold a version spoken slowly. You can also set a limit for how often a sample can be played, define if punctuation should be relevant for scoring, and decide whether small mistakes like typing errors should be counted as no mistake, a full mistake, or just a half mistake.', 'https://h5p.org/sites/default/files/icon_8.svg', 1544698878, 1586415343, 1, 34, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dictation-1_0.png?itok=Wu85TuX8\",\"alt\":\"Initial view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dictation-2_0.png?itok=Mg0TRDj5\",\"alt\":\"Show score\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dictation-3_0.png?itok=eKpbR2t2\",\"alt\":\"Showing solution\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dictation-berries_0.png?itok=m9KR17o8\",\"alt\":\"A dictation about berries\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/dictation', 'https://h5p.org/tutorial-dictation', '[\"language\",\"audio\"]', '[\"Multimedia\",\"Questions\"]', 'otacke'),
(40, 'H5P.BranchingScenario', 1, 2, 0, 1, 20, 'Branching Scenario (beta)', 'Create dilemmas and self paced learning', 'Branching Scenario allow authors to present the learners with a variety of rich interactive content and choices. The choices the learners make will determine the next content they see. May be used to create dilemmas, serious games, and self-paced learning.', 'https://h5p.org/sites/default/files/icon_9.svg', 1551271593, 1560849943, 1, 37, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_0_0.png?itok=eRgwKqbj\",\"alt\":\"Screenshot 01\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_1.png?itok=mDY0Y_ys\",\"alt\":\"Screenshot 02\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_2.png?itok=YVxss_-c\",\"alt\":\"Screenshot 03\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_3.png?itok=TdTgaM87\",\"alt\":\"Screenshot 04\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_4.png?itok=DPsyysid\",\"alt\":\"Screenshot 05\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_5.png?itok=BrCSMC1L\",\"alt\":\"Screenshot 06\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/branching-scenario', 'https://h5p.org/tutorial-branching-scenario', '[]', '[]', 'Joubel'),
(41, 'H5P.ThreeImage', 0, 3, 5, 1, 20, 'Virtual Tour (360)', 'Create 360 environments with interactions', '360 (equirectangular) and normal images may be enriched with interactivities like explanations, videos, sounds, and interactive questions. The images create scenes that also may be linked together to give the user an impression of moving between environments or between different viewpoints within the same environment.', 'https://h5p.org/sites/default/files/360-icon.svg', 1551273005, 1589176511, 1, 40, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_1_0.png?itok=GUmLlxSY\",\"alt\":\"Screenshot 02\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_2_0.png?itok=t5YmuLiK\",\"alt\":\"Screenshot 03\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/screenshot-01_3_0.png?itok=58i8supV\",\"alt\":\"Screenshot 04\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/virtual-tour-360', 'https://h5p.org/tutorial-virtual-tour', '[]', '[\"Games\",\"Multimedia\"]', 'Joubel'),
(42, 'H5P.FindTheWords', 1, 4, 4, 1, 20, 'Find the words', 'Grid word search game', 'A free HTML5-based word search activity that allows authors to create a list of words that will be drawn in a grid. The learner\'s task is to find and select the words in the grid.', 'https://h5p.org/sites/default/files/icon_10.svg', 1563536197, 1563536256, 0, 28, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-words-01.png?itok=O2BgqYiR\",\"alt\":\"Learners has chosen 5 of 12 correct words\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-words-02.png?itok=J9s5I6oM\",\"alt\":\"Show solution mode\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-words-03.png?itok=vz4BrCJd\",\"alt\":\"Greek characters\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/find-the-words', '', '[]', '[\"Games\",\"Questions\"]', 'jithin'),
(43, 'H5P.InteractiveBook', 1, 2, 9, 1, 23, 'Interactive Book', 'Create small courses, books and tests', 'Create small courses, books, or tests. Interactive Book allows authors to combine large amounts of interactive content like interactive videos, questions, course presentations, and more on multiple pages. There is a summary at the end summing up the scores obtained by the learner throughout the book.', 'https://h5p.org/sites/default/files/icon_11.svg', 1596534830, 1596540170, 1, 42, '[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-book-01_1.png?itok=Pe4eW1mb\",\"alt\":\"Interactive book page\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-book-01_2.png?itok=Vz7aDDvh\",\"alt\":\"Interactive book summary page\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-book-01_3.png?itok=nBDvwn__\",\"alt\":\"Footer\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-book-01_4.png?itok=FFNO5sLk\",\"alt\":\"Start screen (book cover)\"}]', '{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}', 'https://h5p.org/content-types/interactive-book', '', '[\"book\",\"course\"]', '[\"Multimedia\",\"Questions\"]', 'fnoks');
